from pathlib import Pathdef CountLinesInFile(FilePath):    Counter = 0    FileHandle = open(FilePath, "r")    for Line in FileHandle:        if Line != '\r\n' and Line != '\n' and Line != '\r':            Counter += 1    return Counterdef CollectAllFiles(FolderPath):    rootdir = Path(FolderPath)    # Return a list of regular files only, not directories    file_list = [f for f in rootdir.glob('**/*') if f.is_file()]    new_file_list = list()    for file in file_list:        fullpath = file.absolute().as_posix()        if not fullpath.__contains__('.idea'):            new_file_list.append(file)    return new_file_listdef main():    file_list = CollectAllFiles('./')    total_count = 0    for file in file_list:        numberoflines = CountLinesInFile(file.absolute().as_posix())        print(file, numberoflines)        total_count += numberoflines    print(total_count)if __name__ == "__main__":    main()